# -*- coding: utf-8 -*-

from odoo import models, fields, api


class staff(models.Model):
    _name = 'hospital_mgmt.staff'
    _rec_name = "staff_id"

    #DEFAULT_IMG = ""
    name = fields.Char(string="Staff Name", required=True)
    gender = fields.Selection([('m', 'Male'), ('f', 'Female')], string="Gender", required=True)
    blood_group = fields.Char(string="Blood Group", required=True)
    staff_type = fields.Many2one("hospital_mgmt.staff_type", ondelete='restrict', string="Staff Type", index=True, required=True)
    staff_image = fields.Binary(default="")
    staff_id = fields.Char(required=True, readonly=True, copy=False, default="New")


class patient(models.Model):
    _name = "hospital_mgmt.patient"

    name = fields.Char(string="Name", ondelete='restrict')
    patient_id = fields.Char(required=True, readonly=True, copy=False, default="New")
